// src/middleware/tenant-context.ts
import { Context, Next } from 'hono'
import { HTTPException } from 'hono/http-exception'

interface Tenant {
  id: string
  name: string
  subdomain: string
  settings: {
    features: string[]
    limits: {
      users: number
      storage: number
    }
  }
