  try {
    const result = await importData(file)
    return c.json({ success: true, result })
  } catch (error) {
    if (error instanceof ValidationError) {
      throw error // Re-throw known errors
    }

    // Wrap unknown errors
    throw new HTTPException(500, {
      message: 'Import failed',
      cause: error
    })
  }
})
