```typescript
import { z } from 'zod'
import { zValidator } from '@hono/zod-validator'

// Custom error formatting
const validationHook = (result: any, c: any) => {
  if (!result.success) {
    return c.json(
      {
        error: 'Validation failed',
        details: result.error.flatten()
      },
      400
    )
  }
}

app.post('/users',
  zValidator('json', createUserSchema, validationHook),
  (c) => {
