    return c.json({ user }, 201)
  }
)

// Global error handler for validation
app.onError((err, c) => {
  if (err instanceof z.ZodError) {
    return c.json(
      {
        error: 'Validation error',
        issues: err.issues
      },
      400
    )
  }
  return c.json({ error: 'Internal server error' }, 500)
})
